package main

import (
    "math/rand"
    "os"

    "github.com/ajstarks/svgo"
)

var (
    canvas = svg.New(os.Stdout)
    width  = 500
    height = 500
)

func main() {
    canvas.Start(width, height)
    canvas.Gstyle("fill:white")
    var color string
    radius := 80
    step := 8
    for i := 0; i < 200; i++ {
        if i%4 == 0 {
            color = "rgb(127,0,0)"
        } else {
            color = "rgb(0,127,0)"
        }
        x, y := rand.Intn(width), rand.Intn(height)
        for r, nc := radius, 0; nc < 10; nc++ {
            canvas.Circle(x, y, r, "stroke:"+color)
            r -= step
        }
    }
    canvas.Gend()
    canvas.End()
}

package main

import (
    "github.com/ajstarks/svgo"
    "os"
)

var (
    canvas = svg.New(os.Stdout)
    width  = 500
    height = 500
)

func main() {
    y := 20
    v := 10
    canvas.Start(width, height)
    canvas.Rect(0, 0, width, height, "fill:rgb(240,240,240)")
    canvas.Gstyle("stroke:white")
    for x := 20; x < 450; x += 30 {
        op := 0.1
        for i := 0; i < 100; i += 10 {
            canvas.Square(x, y, 20*2, canvas.RGBA(v, 0, 0, op))
            y += 30
            op += 0.1
        }
        y = 20
        v += 25
    }
    canvas.Gend()
    canvas.End()
}

