package main

import (
    "math"
    "os"

    "github.com/ajstarks/svgo"
)

var (
    canvas = svg.New(os.Stdout)
    width  = 500
    height = 500
)

// See: http://vormplus.be/blog/article/processing-month-day-4-stars
func star(xp, yp, n int, inner, outer float64, style string) {
    xv, yv := make([]int, n*2), make([]int, n*2)
    angle := math.Pi / float64(n)
    for i := 0; i < n*2; i++ {
        fi := float64(i)
        if i%2 == 0 {
            xv[i] = int(math.Cos(angle*fi) * outer)
            yv[i] = int(math.Sin(angle*fi) * outer)
        } else {
            xv[i] = int(math.Cos(angle*fi) * inner)
            yv[i] = int(math.Sin(angle*fi) * inner)
        }
    }
    canvas.Translate(xp,yp);  canvas.Polygon(xv, yv, style); canvas.Gend()
}

func main() {
    canvas.Start(width, height)
    for x, op, i := 50, 1.0, 5; i <= 10; i++ {
        star(x, 200, i*2, 20, 40, canvas.RGBA(0,0,127,op))
        star(x, 300, i, 20, 40, canvas.RGBA(127,0,127,op))
        x += 80
        op -= 0.15
    }
    canvas.End()
}

package main
import ( "fmt" "os"
"github.com/ajstarks/svgo" )
var ( canvas = svg.New(os.Stdout) width = 500 height = 500 )
func coord(x, y, size int, label string) { tstyle := "text-anchor:middle:font-size:12pt" offset := size + (size / 2) canvas.Text(x, y-offset, fmt.Sprintf("%s (%d,%d)", label, x, y), tstyle) canvas.Circle(x, y, size) }
func showcurve(bx, by, cx, cy, ex, ey int) { dotsize := 5 sw := dotsize * 2 cfmt := "stroke:%s;stroke-width:%d;fill:none;stroke-opacity:%.2f" style := fmt.Sprintf(cfmt, "red", sw, 0.2) coord(bx, by, dotsize, "begin") coord(ex, ey, dotsize, "end") coord(cx, cy, dotsize, "control") canvas.Qbez(bx, by, cx, cy, ex, ey, style) }
func main() { canvas.Start(width, height) canvas.Rect(0, 0, width, height, "fill:rgb(250,250,250)") canvas.Grid(0, 0, width, height, 25, "stroke:lightgray") showcurve(70, 200, 100, 425, 425, 125) canvas.End() }

package main
import ( "fmt" "os"
"github.com/ajstarks/svgo" )
var ( canvas = svg.New(os.Stdout) width = 500 height = 500 )
func coord(x, y, size int) { offset := size * 2 canvas.Text(x, y-offset, fmt.Sprintf("(%d,%d)", x, y), "font-size:50%;text-anchor:middle") canvas.Circle(x, y, size, "fill-opacity:0.3") }
func makepath(x, y, sx, sy, cx, cy, ex, ey int, id, text string) { canvas.Def() canvas.Qbez(sx, sy, cx, cy, ex, ey, `id="`+id+`"`) canvas.DefEnd() canvas.Translate(x, y) canvas.Textpath(text, "#"+id) coord(sx, sy, 5) coord(ex, ey, 5) coord(cx, cy, 5) canvas.Gend() }
func main() { message := `It's fine & "dandy" to have text on a path` canvas.Start(width, height) canvas.Gstyle("font-family:serif;font-size:21pt") makepath(0, 0, 70, 200, 100, 425, 425, 125, "tpath", message) canvas.Gend() canvas.End() }

package main
import ( "os"
"github.com/ajstarks/svgo" )
var ( canvas = svg.New(os.Stdout) width = 500 height = 375 )
func dot(x, y, d int) { canvas.Circle(x, y, d/2, "fill:rgb(128,0,128)") }
// Composition from "Design for Hackers, pg. 129 func main() { d1 := height d2 := d1 / 4 d3 := (d2 * 3) / 4 d4 := (d3 * 3) / 4
coffset := height / 6 hoffset := height / (height / 10) voffset := -width / 10
canvas.Start(width, height) canvas.Rect(0, 0, width, height, "fill:lightblue") dot(width-coffset, height-coffset, d1) dot(width/2, height/3, d2) dot(width/4, height*2/3, d3) dot(width/4+hoffset, height/3+voffset, d4) canvas.Grid(0, 0, width, height, width/4, "stroke:red") canvas.End() }
